CREATE PROCEDURE [dbo].[Activity_Get]
	@Identifier	BIGINT
AS

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
-- Insert statements for procedure here SELECT info.* FROM Activity a CROSS APPLY GetActivity(a.Id) info LEFT JOIN ActivityUserGroup aug ON aug.ActivityId = a.Id WHERE (@Owner IS NULL OR @Owner = a.Owner_id) AND (@UserGroupId IS NULL OR @UserGroupId = aug.UserGroupId) AND (@AreaId IS NULL OR @AreaId = a.AreaId) AND (@StartCreationDate IS NULL OR @StartCreationDate <= a.CreationDate) AND (@EndCreationDate IS NULL OR @EndCreationDate >= a.CreationDate);
END

CREATE PROCEDURE [dbo].[Activity_List]
	@Owner	BIGINT = NULL,
	@UserGroupId BIGINT = NULL,
	@AreaId	BIGINT = NULL,
	@StartCreationDate DATETIME = NULL,
	@EndCreationDate DATETIME = NULL
AS

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
IF @Identifier IS NULL OR @Identifier <= 0 INSERT INTO Activity (Title, CreationDate, DeliveryTime, Description, Owner_id) VALUES (@Title, @CreationDate, @DeliveryTime, @Description, @Owner) ELSE UPDATE Activity SET Title = @Title, CreationDate = @CreationDate, DeliveryTime = @DeliveryTime, Description = @Description, Owner_id = @Owner WHERE Id = @Identifier
SELECT CAST(SCOPE_IDENTITY() AS BIGINT) END

